    public static void displayCurrentTimeStamp(String str){
        System.out.println(str + " " + System.currentTimeMillis());
    }

    public static void timeStampDifference(long first_time, long second_time, int test_number){
        if(test_number == 0){
            System.out.println("O(n^2) Sort - Time to run: " + (second_time - first_time));
        } else{
            System.out.println("O(nlogn) TEST " + test_number + " - Time to run: " + (second_time - first_time));
        }
    }

    /* Check if the tests passed by comparing the arrays */
    public static void didTheTestPass(int[] input, int[] out, int test_number){
        System.out.println(test_if_array_sorted_correctly(input, out) ? "Test" + test_number + " Passed\n" : "Test" + test_number + " Did NOT Pass\n");
    }

    /* Sort the array in O(n*n) time to test the O(nlogn) sorted array */
    public static boolean test_if_array_sorted_correctly(int[] input, int[] array_to_compare_to){
        long first_time = System.currentTimeMillis();
        for(int i = 0; i < input.length; i++){
            for(int j = i+1; j < input.length; j++){
                if(input[j] < input[i]){
                    int temp = input[i];
                    input[i] = input[j];
                    input[j] = temp;
                }
            }
        }
        long second_time = System.currentTimeMillis();
        timeStampDifference(first_time, second_time, 0);
        return Arrays.equals(input, array_to_compare_to);
    }

    /* Print Array */
    public static String printArray(int[] input){
        String string = "";
        for(int i = 0; i < input.length; i++){
            if(i+1 == input.length){
                string += input[i];
            }else{
                string += input[i] + ", ";
            }
        }
        return string;
    }
